changeSet.sql:
CREATE TABLE if not exists customUser ( id bigserial PRIMARY KEY, firstName VARCHAR(50) NOT NULL, lastName VARCHAR(50) NOT NULL, age INT, email VARCHAR(50) UNIQUE NOT NULL, birthday DATE, phone VARCHAR(12), login VARCHAR(50) UNIQUE NOT NULL, password VARCHAR(50) NOT NULL );
Create table if not exists role ( id bigserial PRIMARY KEY, role varchar(20) NOT NULL );
Create table if not exists customUser_role ( user_id bigint NOT NULL, role_id bigint NOT NULL, FOREIGN KEY (user_id) REFERENCES customUser (id), FOREIGN KEY (role_id) REFERENCES role (id) );
Insert into role (role) values ('ROLE_USER'), ('ROLE_ADMIN');
